mapper.insert:
	
insert into member
(
member_id
, password
, name
,nickname
,tel
,address
, role_id
,logintype_id
,category_id
,certify
)
VALUES
(
#{member_id}
, #{password}
, #{name}
, #{nickname}
, #{tel}
,#{address}
,#{roleVO.role_id}
,#{logintypeVO.logintype_id}
,#{categoryVO.category_id}
,#{certify}
)

mapper.select:

select
member_id from member where member_id = #{member_id}
and
certify=#{certify}

mapper.update:

update member set
certify = 'Y' where member_id =
#{member_id}
